import java.util.Arrays;public class BubbleSort extends Util {    public static void bubbleSort(int[] array){        boolean needIter = true;        while(needIter){            needIter = false;            for(int i = 1; i < array.length; i++){                if(array[i] < array[i-1]){                    swap(array, i, i-1);                    needIter = true;                }            }        }        System.out.println(Arrays.toString(array));    }    public static void bubbleSortNeg(int[] array){        boolean needIter = true;        while(needIter){            needIter = false;            for(int i = 1; i < array.length; i++){                if(array[i] > array[i-1]){                    swap(array, i, i-1);                    needIter = true;                }            }        }        System.out.println(Arrays.toString(array));    }}