public class BubbleSort extends Util {    public static int[] bubbleSort(int[] array){        boolean needIter = true;        while(needIter){            needIter = false;            for(int i = 1; i < array.length; i++){                if(array[i] < array[i-1]){                    swap(array, i, i-1);                    needIter = true;                }            }        }        return array;    }    public static int[] bubbleSortNeg(int[] array){        boolean needIter = true;        while(needIter){            needIter = false;            for(int i = 1; i < array.length; i++){                if(array[i] > array[i-1]){                    swap(array, i, i-1);                    needIter = true;                }            }        }        return array;    }}