public class BubleSort extends Util {    public static int[] bubleSort(int[] array){        boolean needIter = true;        while(needIter){            needIter = false;            for(int i = 1; i < array.length; i++){                if(array[i] < array[i-1]){                    swap(array, i, i-1);                    needIter = true;                }            }        }        return array;    }    public static int[] bubleSortNeg(int[] array){        boolean needIter = true;        while(needIter){            needIter = false;            for(int i = 1; i < array.length; i++){                if(array[i] > array[i-1]){                    swap(array, i, i-1);                    needIter = true;                }            }        }        return array;    }    public static int[] bubleSortLimit(int[] array, int start,int stop){        boolean needIter = true;        while(needIter){            needIter = false;            for(int i = start;  i <= stop ; i++){                if(array[i] < array[i-1]){                    swap(array, i, i-1);                    needIter = true;                }            }        }        return array;    }}