public class CountingSort extends Util {    public static int[] countSort(int[] inputArray) {        int n = inputArray.length;        int max = maxFrom(inputArray);        int[] countArray = new int[max + 1];        for (int i = 0; i < n; i++) {            countArray[inputArray[i]]++;        }        for (int i = 1; i <= max; i++) {            countArray[i] += countArray[i - 1];        }        int[] outputArray = new int[n];        for (int i = n - 1; i >= 0; i--) {            outputArray[countArray[inputArray[i]] - 1] = inputArray[i];            countArray[inputArray[i]]--;        }        return outputArray;    }    public static int[] countSortWithNegatives(int[] inputArray) {        int max = maxFrom(inputArray);        int min = 0;        for (int i : inputArray) {            min = Math.min(min, i);        }        int range = max - min + 1;        for (int i = 0; i < inputArray.length; i++) {            inputArray[i] -= min;        }        int[] outputArray = new int[inputArray.length];        int[] countArray = new int[range];        for (int num : inputArray) {            countArray[num]++;        }        for (int i = 1; i < range; i++) {            countArray[i] += countArray[i - 1];        }        for (int i = inputArray.length - 1; i >= 0; i--) {            outputArray[countArray[inputArray[i]] - 1] = inputArray[i] + min;            countArray[inputArray[i]]--;        }        return outputArray;    }}