import java.util.Arrays;public class LinearSort extends Util{    static void linearSort(int[] array){        for(int left = 1; left< array.length; left++){            int value = array[left];            int i = left - 1;            for(; i >= 0; i--){                if(value < array[i]){                    array[i+1] = array[i];                }else{                    break;                }            }array[i+1] = value;        }        System.out.println(Arrays.toString(array));    }    static void linearSortNeg(int[] array){        for(int left = 1; left< array.length; left++){            int value = array[left];            int i = left - 1;            for(; i >= 0; i--){                if(value > array[i]){                    array[i+1] = array[i];                }else{                    break;                }            }array[i+1] = value;        }        System.out.println(Arrays.toString(array));    }}