import java.util.Arrays;public class MergeSort extends Util {    static void mergeSort(int arr[]){        if(arr.length == 1 ){            System.out.println(Arrays.toString(arr));        }        int mid = arr.length/2;        int arrLeft[] = new int [mid];        int arrRight[] = new int [arr.length-mid];        for(int i=0; i<mid; i++ ){            arrLeft[i] = arr[i];        }        for(int i=mid; i<arr.length; i++ ){            arrRight[i-mid] = arr[i];        }        if (!(arrLeft.length <=1) && !(arrRight.length<=1)){            mergeSort(arrLeft);            mergeSort(arrRight);        }        merge(arr, arrLeft, arrRight);    }}