public class RadixSort {    public static void radixSort(int[] array) {        if (array == null || array.length == 0) {            return;        }        int max = Math.abs(array[0]);        for (int num : array) {            max = Math.max(max, Math.abs(num));        }        for (int exp = 1; max / exp > 0; exp *= 10) {            countingSortByDigit(array, exp);        }    }    public static void countingSortByDigit(int[] array, int exp) {        int n = array.length;        int[] output = new int[n];        int[] count = new int[19];        for (int num : array) {            int digit = (num / exp) % 10;            count[digit + 9]++;        }        for (int i = 1; i < 19; i++) {            count[i] += count[i - 1];        }        for (int i = n - 1; i >= 0; i--) {            int digit = (array[i] / exp) % 10;            output[count[digit + 9] - 1] = array[i];            count[digit + 9]--;        }        System.arraycopy(output, 0, array, 0, n);    }}